mapper.select:
select 

from LDMENUGRPTOMENUA
where MENUGRPCODE = #{menugrpcode,jdbcType=VARCHAR}
  and NODECODE = #{nodecode,jdbcType=VARCHAR}

select n.sub_menu as nodecode,n.meddle_menu as meddlemenu,n.menu_desc as menuDesc from  menu n where n.sub_menu 
in  #{AList}


and n.meddle_menu
in  #{BList}

mapper.delete:
delete from LDMENUGRPTOMENUA
where MENUGRPCODE in (select menugrpcode from LDMENUGRPA where  menugrpname=#{menugrpcode,jdbcType=VARCHAR}) 
  and NODECODE = #{nodecode,jdbcType=VARCHAR}
  and MEDDLEMENU=#{meddlemenu}
  and MENUDESC=#{menuDesc}

delete from LDMENUGRPTOMENUA
where MENUGRPCODE = #{menugrpcode,jdbcType=VARCHAR}
  and NODECODE = #{nodecode,jdbcType=VARCHAR}

mapper.update:
update LDMENUGRPTOMENUA


    MAKEDATE = #{makedate,jdbcType=DATE},


    MAKETIME = #{maketime,jdbcType=VARCHAR},


where MENUGRPCODE = #{menugrpcode,jdbcType=VARCHAR}
  and NODECODE = #{nodecode,jdbcType=VARCHAR}

update LDMENUGRPTOMENUA
set MAKEDATE = #{makedate,jdbcType=DATE},
  MAKETIME = #{maketime,jdbcType=VARCHAR}
where MENUGRPCODE = #{menugrpcode,jdbcType=VARCHAR}
  and NODECODE = #{nodecode,jdbcType=VARCHAR}
