mapper.update:
update LCACCOUNTINFO


    INSURCECOM = #{insurcecom,jdbcType=VARCHAR},


    ACCRATE = #{accrate,jdbcType=VARCHAR},


    BAK1 = #{bak1,jdbcType=VARCHAR},


    BAK2 = #{bak2,jdbcType=VARCHAR},


    BAK3 = #{bak3,jdbcType=VARCHAR},


    BAK4 = #{bak4,jdbcType=VARCHAR},


    BAK5 = #{bak5,jdbcType=VARCHAR},


where TRANSNO = #{transno,jdbcType=VARCHAR}
  and PROPOSALCONTNO = #{proposalcontno,jdbcType=VARCHAR}
  and RISKCODE = #{riskcode,jdbcType=VARCHAR}
  and ACCNO = #{accno,jdbcType=VARCHAR}

update LCACCOUNTINFO
set INSURCECOM = #{insurcecom,jdbcType=VARCHAR},
  ACCRATE = #{accrate,jdbcType=VARCHAR},
  BAK1 = #{bak1,jdbcType=VARCHAR},
  BAK2 = #{bak2,jdbcType=VARCHAR},
  BAK3 = #{bak3,jdbcType=VARCHAR},
  BAK4 = #{bak4,jdbcType=VARCHAR},
  BAK5 = #{bak5,jdbcType=VARCHAR}
where TRANSNO = #{transno,jdbcType=VARCHAR}
  and PROPOSALCONTNO = #{proposalcontno,jdbcType=VARCHAR}
  and RISKCODE = #{riskcode,jdbcType=VARCHAR}
  and ACCNO = #{accno,jdbcType=VARCHAR}
