mapper.select:
select 

from LAPRODQUALI
where 1=1

  and riskcode = #{riskcode,jdbcType=VARCHAR}


  and QUALIFITYPE = #{qualifitype,jdbcType=VARCHAR}

mapper.delete:
delete from LAPRODQUALI
where 1=1

and riskcode = #{riskcode,jdbcType=VARCHAR}


  and QUALIFITYPE = #{qualifitype,jdbcType=VARCHAR}

mapper.update:
update LAPRODQUALI
set 
  QUALIFITYPE = #{qualifitype,jdbcType=VARCHAR},
  FLAG = #{flag,jdbcType=VARCHAR},
  MODIFYDATE = #{modifydate,jdbcType=DATE},
  MODIFYTIME = #{modifytime,jdbcType=VARCHAR}
where riskcode = #{riskcode,jdbcType=VARCHAR}

  and  QUALIFITYPE = #{myqualifitype,jdbcType=VARCHAR}


  and  QUALIFITYPE = #{qualifitype,jdbcType=VARCHAR}
