mapper.select:
select
from latraincourse where 1=1
and coursecode = #{coursecode}

	select

	from t_sys_user
	where 1=1


        and "user_code" = #{userCode}


        and "user_name" = #{userName}

mapper.update:
update latraincourse


    coursetitle= #{coursetitle,jdbcType=VARCHAR},


    flag = #{flag,jdbcType=VARCHAR},


    modifydate= #{modifydate,jdbcType=TIMESTAMP},


    modifytime= #{modifytime,jdbcType=VARCHAR},


where coursecode=#{coursecode,jdbcType=VARCHAR} 

 and coursetitle=#{coursetitle,jdbcType=VARCHAR} 


 and coursetitle=#{mycoursetitle,jdbcType=VARCHAR}
