mapper.select:
select 

from LCADDRESS
where TRANSNO = #{transno,jdbcType=VARCHAR}
and CUSTOMERNO=#{customerno,jdbcType=VARCHAR}
and ADDRESSNO=#{addressno,jdbcType=DECIMAL}

mapper.delete:
delete from LCADDRESS
where TRANSNO = #{transno,jdbcType=VARCHAR}
and CUSTOMERNO=#{customerno,jdbcType=VARCHAR}
and ADDRESSNO=#{addressno,jdbcType=DECIMAL}
